import React from 'react'

import '../Assets/CSS/Footer.css'

import LogoWhite from '../Assets/Images/Logo-Gandiwa-White.png'

class Footer extends React.Component{

    constructor(){
        super()
        this.state = {
            MouseIn1: false,
            MouseIn2: false,
            MouseIn3: false
        }

    }

    MouseIn1 (e) {
        this.setState({
            MouseIn1: true
        });
        e.preventDefault()
    }

    MouseIn2 (e) {
        this.setState({
            MouseIn2: true
        });
        e.preventDefault()
    }

    MouseIn3 (e) {
        this.setState({
            MouseIn3: true
        });
        e.preventDefault()
    }

    MouseOut1 (e) {
        this.setState({
            MouseIn1: false
        });
        e.preventDefault()
    }

    MouseOut2 (e) {
        this.setState({
            MouseIn2: false
        });
        e.preventDefault()
    }

    MouseOut3 (e) {
        this.setState({
            MouseIn3: false
        });
        e.preventDefault()
    }

    render(){

        const iconColor1 = {
            color: "white"
        }

        const iconColor2 = {
            color: "white"
        }

        const iconColor3 = {
            color: "white"
        }

        if(this.state.MouseIn1){
            iconColor1.color= "#e9a6ff"
        }
        else if(!this.state.MouseIn1){
            iconColor1.color= "white"
        }

        return(    
            <footer>
                <div className="foot-container">
                    <div className="foot-logo">
                        <img src = {LogoWhite} className="foot-img" />
                    </div>
                    <div className="foot-info">
                        Centennial Tower, 29th Floor Jl Jend Gatot Subroto no 24-25, Jakarta Selatan 12950
                        <br />
                        <div className="foot-phone">
                            021-29608389
                        </div>
                    </div>

                    <div className="foot-menu-1">
                        Home
                    </div>
                    <div className="foot-menu-2">
                        Smart Media
                    </div>
                    <div className="foot-menu-3">
                        Influencer
                    </div>
                    <div className="foot-menu-4">
                        Klien
                    </div>
                    <div className="foot-menu-5">
                        Kontak
                    </div>
                    <div className="foot-menu-6">
                        Karir
                    </div>
                    <div className="foot-menu-7">
                        Ketentuan & Kebijakan
                    </div>
                    <div className="foot-menu-8">
                        Kebijakan Privasi
                    </div>

                    <div className="foot-line">
                        <div className="foot-line-white" />
                    </div>
                    <div className="foot-sosmed-1">
                        <div className="foot-circle">
                            <div className="foot-sosmed-container">
                                <div className="foot-sosmed-img">
                                    <i class="fab fa-facebook-f"></i>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="foot-sosmed-2">
                        <div className="foot-circle">
                            <div className="foot-sosmed-container">
                                <div className="foot-sosmed-img">
                                    <i class="fab fa-instagram"></i>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="foot-sosmed-3">
                        <div className="foot-circle" onMouseEnter={this.MouseIn3} onMouseLeave={this.MouseOut3}>
                            <div className="foot-sosmed-container">
                                <div className="foot-sosmed-img">
                                     <i class="fab fa-linkedin-in"></i>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div className="foot-copyright">
                        &copy; Copyright 2020 PT Gandiwa Nusantara Mediatek . All Right Reserved
                    </div>
                </div>
            </footer>
        )
    }
}

export default Footer
